
public class Maths {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.abs(-24.5));
		System.out.println(Math.ceil(24.4));
		System.out.println(Math.floor(24.3));
		System.out.println(Math.min(24.5,23.5));
		System.out.println(Math.max(-24.5,-34.5));
		System.out.println(Math.pow(5,3));
		System.out.println(Math.sqrt(16));
	}

}
